</template>

<script>
import store from "../store";

export default {
  name: "Dashboard",
  data() {
    return {
      sharedState: store.state,
      query: ""
    };
  },
  computed: {
    filteredNodes() {
      // Filter the containers inside of each node to match the query box value
      return this.sharedState.nodes.map(node => {
        node.Containers = node.Containers.filter(container => {
          // Check if each container containers the value
          if (
            container.Image.toLowerCase().indexOf(this.query.toLowerCase()) > -1
          ) {
            return true;
          }
        });
        return node;
      });
    }
  }
};
</script>

<style lang="scss">
/* Colors */
$blue: #099cec;
$warning: #f5d000;
$success: #08ce76;
$danger: #ef4a53;

.container {
  border-top: 4px solid $blue;
  background-color: #f7f7f7;
  padding-top: 1em;
  height: 100%;
  width: 100%;

  .offline {
    width: 75%;
    margin: 40px auto;
  }
}

.refiners form {
  padding: 0.7em;
  text-align: left;
  display: flex;
  flex-direction: row;

  input[type="text"] {
    width: 12em;
    padding: 0.3em;
    font-size: 1.25em;
    background: transparent;
    border: 0;
    border-bottom: 1px solid #ccc;
    margin-right: 3em;

    &::placeholder {
      font-size: 0.8em;
    }
  }

  button {
    justify-content: center;
    line-height: 24px;
    min-height: 32px;
    max-height: 32px;
    white-space: nowrap;
    z-index: 1;
    align-items: center;
    color: rgb(9, 156, 236);
    cursor: pointer;
    display: flex;
    font-size: 13px;
    text-transform: capitalize;
    position: relative;
    margin: 0px;
    outline: none;
    padding: 3px 14px;
    border-width: 1px;
    border-style: solid;
    border-image: initial;
    transition: color 0.3s, border 0.3s ease-in-out;
    background: rgb(255, 255, 255);
    border-color: rgb(9, 156, 236);
    border-radius: 0px;
  }
}

.nodes {
  height: 90vh;
  display: flex;
  flex-direction: row;
  flex-grow: 2;
  align-items: stretch;
  flex-wrap: nowrap;
  overflow-x: auto;

  > div {
    flex: 0 0 auto;
  }

  .os {
    transform: translateY(5px);
    display: inline-block;
    margin: 0 5px 0 10px;

    svg {
      width: 25px;
    }
  }

  .orchestrator-swarm {
    height: 25px;
    position: relative;
    top: 5px;
    margin-right: 5px;
  }

  .orchestrator-kubernetes {
    height: 25px;
    position: relative;
    top: 5px;
  }

  .header {
    margin: 0.7em 0.7em 0;
    min-width: 12em;
    flex: 0 0 auto;
    box-shadow: 0 0 4px 2px rgba(0, 0, 0, 0.05);
    overflow: scroll;
    background-color: #fff;
    white-space: nowrap;

    h1 {
      color: $blue;
      font-size: 1em;
      padding-bottom: 0.75em;
      border-bottom: 2px solid #ddd;
      padding: 0.5em 1.2em;
      margin: 0;
    }
  }

  .containers {
    margin: 0 0.7em 0.7em;
    min-width: 12em;
    flex: 0 0 auto;
    box-shadow: 0 0 4px 2px rgba(0, 0, 0, 0.05);
    overflow-y: scroll;
    height: 100%;
    background-color: #fff;
  }

  div ul {
    display: flex;
    flex-direction: column;
    margin: 0;
    padding: 0;

    li {
      background: #fff;
      padding: 0.9em;
      border-bottom: 2px solid #ddd;
      display: inline-block;
    }

    li h3 {
      margin: 0;
      padding: 0.5em 0;
      font-size: 1.3em;
    }

    li span {
      margin-bottom: 0.5em;
      word-break: break-all;
    }

    li span:first-child {
      margin-top: 0.5em;
    }

    li span:last-child {
      color: rgb(152, 109, 252);
      font-weight: 900;
    }

    li.empty {
      border: none;
    }
    li.empty span {
      color: #ddd;
      font-size: 2em;
    }

    .tag span:first-child::before {
      content: "";
      display: inline-block;
      width: 12px;
      height: 12px;
      border-radius: 12px;
      background-color: #ddd;
      margin-right: 10px;
    }

    .empty span:first-child::before {
      display: none;
    }

    li.running span:first-child::before {
      background-color: $success;
    }

    li.created span:first-child::before,
    li.preparing span:first-child::before {
      background-color: $blue;
    }

    li.paused span:first-child::before,
    li.restarting span:first-child::before {
      background-color: $warning;
    }

    li.exited span:first-child::before,
    li.dead span:first-child::before {
      background-color: $danger;
    }

    .tag span:last-child {
      margin-left: 0.8em;
    }
  }

  div {
    &.active .header {
      border-top: 5px solid $success;
    }

    &.pause .header {
      border-top: 5px solid $blue;
    }

    &.drain .header {
      border-top: 5px solid $warning;
    }
  }

  li {
    list-style: none;
    padding: 0;
    margin: 0;
  }
}

/* VueJS Animations */
.list-enter-active,
.list-leave-active {
  transition: all 0.3s ease;
}
.list-enter,
.list-leave-to {
  opacity: 0;
  transform: translateY(30px);
}
.list-move {
  transition: transform 1s;
}
.list-item {
